import "./ContentHeader.css";
import facebook from "./img/facebook.png";
import twitter from "./img/twitter.png";
import youtube from "./img/youtube.png";
import headerImage from "./img/headerBG.png";
import headerLogo from "./img/headerLogo.png";
import iconI from "./img/information.png";
import closeBTN from "./img/closeBtn.png";
import { useState } from "react";

function ContentHeader(props) {
  const [popUp, SetPopUp] = useState(false);
  function ChangePopUp() {
    SetPopUp(!popUp);
  }
  return (
    <header
      className="contH"
      style={{ backgroundImage: `url(${headerImage})` }}
    >
      {popUp && (
        <div className="InfoWindow">
          <div className="closeBTN">
            <img
              src={closeBTN}
              width={"5%"}
              height={"5%"}
              alt=""
              onClick={ChangePopUp}
            />
          </div>
          <p>დანახარჯები-გამოშვების ცხრილი და მულტიპლიკატორები</p>
          <p>დანახარჯები-გამოშვების  (IO) ცხრილი  1930-იან წლებში შეიქმნა ვასილი ლეონტიევის მიერ, რისთვისაც 1973 წელს ნობელის პრემია მიიღო ეკონომიკის დარგში. დანახარჯები-გამოშვების ცხრილი ხშირად მოიხსენიება, როგორც ლეონტიევის მოდელი. დღესდღეობით აღნიშნული მოდელი მნიშვნელოვანი და ხშირად გამოყენებადი ინსტრუმენტია  ეკონომიკაში მიმდინარე მოვლენებისა და მაკროინდიკატორების შეფასებისათვის. </p>
          <p>დანახარჯების-გამოშვების ცხრილი შედგება წრფივი განტოლებებისაგან, რომელიც გამოსახავს ეკონომიკაში საქონლისა და მომსახურების წარმოებას და მის განაწილებას მომხმარებლებს შორის. მოცემული მოდელი კარგად აღწერს დარგთაშორის დამოკიდებულებას და ტექნოლოგიური დანახარჯების კოეფიციენტის გამოყენებით გვიჩვენებს  ერთი ერთეული i პროდუქტის საწარმოებლად რამდენი ერთეული j პროდუქტია საჭიროა. მოდელზე დაყრდნობით განისაზღვრება ცალკეული დარგის პროდუქციის საბოლოო მოხმარების ცვლილება რა გავლენას ახდენს სხვა დარგების გამოშვებაზე. დანახარჯები გამოშვების ცხრილი მნიშვნელოვანი ინსტრუმენტია საბოლოო მოხმარებაში ეგზოგენური შოკის გამოშვებაზე გავლენის შესაფასებლად. </p>
          <p>დანახარჯები-გამოშვების ცხრილი (IOT) რესურსებისა და გამოყენების ცხრილებს ეფუძნება, რისთვისაც  საჭიროა ცხრილების საბაზისო ფასებში გადაყვანა იმპორტის, პროდუქციაზე გადასახადებისა და სუბსიდიების და სავაჭრო მარჟების გამოკლებით. ცხრილების საბაზისო ფასების მიღების შემდეგ გამოყენებულია ფიქსირებული გაყიდვების სტრუქტურის დაშვება (მოდელი D). რომლის მიხედვითაც თითოეულ პროდუქტს აქვს მისთვის დამახასიათებელი გაყიდვების სტრუქტურა, მიუხედავად იმისა რომელმა დარგმა აწარმოა.</p>
          <p >დანახარჯები-გამოშვების ცხრილი მოიცავს შემდეგ ცხრილებს:</p>
          <ul>
            <li>ადგილობრივი წარმოების ცხრილი;</li>
            <li>იმპორტის ცხრილი;</li>
            <li>ადგილობრივი წარმოების მოხმარება;</li>
            <li>იმპორტის ცხრილი;</li>

        </ul>
        </div>
      )}

      <div className="divHeaderCont">
        <div className="headerContentSmall">
          <a
            href="http://eap.geostat.ge/"
            target="_blank"
            rel="noreferrer"
            className="divHeaderCont1"
          >
            <img src={headerLogo} alt="" />
          </a>

          <div className="divHeaderCont2">
            {!props.languageChange ? (
              <p>ეკონომიკური ანალიზის პორტალი</p>
            ) : (
              <p>Economic Analysis Portal</p>
            )}
          </div>
          <div className="imgDiv">
            <div className="Info">
              <img
                src={iconI}
                alt=""
                width={"100%"}
                height={"100%"}
                onClick={ChangePopUp}
              />
            </div>
            <a
              href="https://www.facebook.com/geostat.ge/"
              target="_blank"
              rel="noreferrer"
            >
              <img src={facebook} alt="" width={"100%"} height={"100%"} />
            </a>
            <a
              href="https://twitter.com/Geostat100"
              target="_blank"
              rel="noreferrer"
            >
              <img src={twitter} alt="" width={"100%"} height={"100%"} />
            </a>
            <a
              href="https://www.youtube.com/user/CensusGe"
              target="_blank"
              rel="noreferrer"
            >
              <img src={youtube} alt="" width={"100%"} height={"100%"} />
            </a>
            {!props.languageChange ? (
              <img
                src={"http://juniors.geostat.ge/assets/img/icons/us.svg"}
                alt=""
                width={"100%"}
                height={"100%"}
                onClick={() => props.SetLanguageChange(!props.languageChange)}
              />
            ) : (
              <img
                src={"http://juniors.geostat.ge/assets/img/icons/ge.svg"}
                alt=""
                width={"100%"}
                height={"100%"}
                onClick={() => props.SetLanguageChange(!props.languageChange)}
              />
            )}
          </div>
        </div>
      </div>
    </header>
  );
}
export default ContentHeader;
